var gr = current.requested_by;
var ra = new GlideRecord("sys_user_role");  
ra.addQuery('name','x_pvi_hr_applicati.hiring_manager');  
ra.query();  
while(ra.next()){
var ro=new GlideRecord("sys_user_has_role");
ro.addQuery("role",ra.sys_id);
ro.query();
while(ro.next()){
if(gr==ro.user){
	answer=true;
	break;
}
else
	answer= false;
}
}

